(()=>{"use strict";window.addEventListener("load",()=>{const e=document.querySelector("#play"),t=document.querySelector("#pause"),o=new Audio("assets/sounds/Sara Montiel - Fumando Espero.mp3");e.addEventListener("click",()=>{o.play(e)}),t.addEventListener("click",()=>{o.pause(t)});const n=document.querySelector(".credits-p"),r=(new Date).getFullYear();n.innerHTML=`Desarrollado por <a href="https://daniel-espanadero.com/" \n        target="_BLANK">Daniel Espa√±adero</a> &copy ${r}`;const c=document.querySelector(".hidden");document.querySelector(".concha").addEventListener("click",()=>{window.innerWidth<768?c.classList.toggle("hidden"):c.classList.remove("hidden")});const s=document.querySelectorAll(".links-menu");for(const e of s)e.addEventListener("click",t=>{t.preventDefault();const o=e.getAttribute("href"),n=document.querySelector(o).offsetTop;scroll({top:n,behavior:"smooth"})});let l=document.querySelectorAll(".mostrar");window.addEventListener("scroll",function(){let e=document.documentElement.scrollTop;for(let t=0;t<l.length;t++)l[t].offsetTop+500<e&&(l[t].style.opacity=1,l[t].classList.add("mostrarArriba"))})})})();